INSERT INTO `testimonials` VALUES (1,'Nieves Marisol Bautista Huaman','SAP HCM y Excel','https://annadoctora.com/storage/photos/1/Testimonios/recomendacion1.png','TÉCNICA EN ADMINISTRACIÓN DE empresas','<p>&quot;El desarrollo de las clases fueron muy <strong>din&aacute;micas y entendibles.</strong>&nbsp;Durante el curso hubo un&nbsp;<strong>trato coordial y apoyo inmediato&nbsp;</strong>de parte de coordinaci&oacute;n acad&eacute;mica ante mis consultas&rdquo;.</p>',3,1,'2022-04-20 12:40:20','2022-04-29 09:21:27'),(2,'Pedro Flores Ipanaque','SAP PM','https://annadoctora.com/storage/photos/1/Testimonios/recomendacion2.png','Técnico Electricista','<p>&ldquo;Me sorprendi&oacute; que <strong>no era d&iacute;ficil aprender SAP</strong>. Con mi capacitaci&oacute;n en Summa Center&nbsp;<strong>logr&eacute; postular al &aacute;rea de planeamiento&nbsp;</strong>de mi empresa, aportando mis conocimentos de SAP PM&nbsp;<strong>pude mejorar</strong>&nbsp;los puntos de medici&oacute;n correctamente&rdquo;.</p>',2,2,'2022-04-20 12:42:14','2022-04-29 09:21:35'),(3,'Jean Paul Martínez Paula','SAP MM','https://annadoctora.com/storage/photos/1/Testimonios/jean-paul-martinez.png','supervisor de despacho y Logística','<p>&ldquo;Gracias al curso que llev&eacute; <strong>escal&eacute; r&aacute;pidamente a un nuevo cargo profesional,</strong> ya que le di&oacute; un mayor peso a mi curr&iacute;culum frente a los reclutadores, los cuales <strong>me promovieron al demostrarles mis nuevos &nbsp;conocimientos</strong> adquiridos. Valoro mucho la atenci&oacute;n especial que la instituci&oacute;n le presta al alumno&rdquo;.</p>',5,3,'2022-04-27 11:01:59','2022-04-29 09:21:43'),(4,'Marco Piero Aarón Gonzáles Uscamayta','SAP MM','https://annadoctora.com/storage/photos/1/Testimonios/marco-piero-aaron.png','Profesional de Negocios Internacionales','<p>&ldquo;El curso <strong>impact&oacute; en mi puesto de trabajo de forma positiva</strong>, ayudando a desenvolverme con eficiencia y <strong>brind&aacute;ndome mejores oportunidades </strong>en mi &aacute;rea laboral&rdquo;.</p>',5,4,'2022-04-27 11:03:04','2022-04-29 09:21:52'),(5,'Franco Andrés Valdez Chumpitaz','SAP MM Y SAP PM','https://annadoctora.com/storage/photos/1/Testimonios/franco-andres-valdez.png','Bachiller UNIVERSITARIO EN administración','<p><strong>&ldquo;La plataforma educativa es innovadora</strong>, mantiene informado al alumno de su avance acad&eacute;mico y lo impulsa a desarrollar de manera constante cada tema. <strong>Recomiendo el curso y valoro la din&aacute;mica del profesor</strong> para explicar cada clase mediante ejemplos sencillos hasta m&aacute;s avanzados&rdquo;.</p>',5,5,'2022-04-27 11:04:11','2022-04-29 09:22:02'),(6,'Marco Paul De Jesús Arévalo Santa María','SAP FI Y SAP HCM','https://annadoctora.com/storage/photos/1/Testimonios/marco-paul-de-jesus.png','Profesional de Negocios Internacionales','<p>&ldquo;Lo que m&aacute;s destaco de Summa Center es su <strong>gran compromiso con el alumno</strong>, desde el primer d&iacute;a estuvieron cuidando mi formaci&oacute;n acad&eacute;mica, pregunt&aacute;ndome si todo marchaba bien o hab&iacute;a algo que no entend&iacute;a para que me pudieran explicar y cuando lo necesitaba <strong>me atend&iacute;an de forma inmediata&rdquo;.</strong></p>',4,6,'2022-04-27 11:05:31','2022-04-29 09:22:10');
